import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../App.css";

function OrganizationPage({ user }) {
  const navigate = useNavigate();

  const [users, setUsers] = useState([]);
  const [tournaments, setTournaments] = useState([]);
  const [organizationName, setOrganizationName] = useState("");
  const [availableUsers, setAvailableUsers] = useState([]);
  const [selectedAddUser, setSelectedAddUser] = useState("");
  const [selectedRemoveUser, setSelectedRemoveUser] = useState("");

  const isAdmin = user.role === "admin";

  // Load data
  useEffect(() => {
    const loadData = async () => {
      try {
        const [usersRes, tournamentsRes, orgRes] = await Promise.all([
          axios.get("http://localhost:5000/api/users"),
          axios.get("http://localhost:5000/api/tournaments"),
          axios.get("http://localhost:5000/api/organizations")
        ]);

        // Users in current organization
        const orgUsers = usersRes.data.filter(u => u.organization_id === user.organization_id);

        // Users not in any organization
        const freeUsers = usersRes.data.filter(u => !u.organization_id);

        // Tournaments in current organization
        const orgTournaments = tournamentsRes.data.filter(t => t.organization_id === user.organization_id);

        // Organization name
        const org = orgRes.data.find(o => o.id === user.organization_id);

        setUsers(orgUsers);
        setTournaments(orgTournaments);
        setAvailableUsers(freeUsers);
        setOrganizationName(org ? org.name : "Unknown");
      } catch (err) {
        console.error(err);
      }
    };

    loadData();
  }, [user.organization_id]);

  // Add user to organization
  const handleAddUser = async () => {
    if (!selectedAddUser) return;

    try {
      await axios.put("http://localhost:5000/api/org/addUser", {
        userId: selectedAddUser,
        orgId: user.organization_id
      });

      setSelectedAddUser("");

      // Reload users
      const usersRes = await axios.get("http://localhost:5000/api/users");
      setUsers(usersRes.data.filter(u => u.organization_id === user.organization_id));
      setAvailableUsers(usersRes.data.filter(u => !u.organization_id));
    } catch (err) {
      console.error(err);
      alert("Failed to add user");
    }
  };

  // Remove user from organization
  const handleRemoveUser = async () => {
    if (!selectedRemoveUser) return;

    try {
      await axios.put("http://localhost:5000/api/org/removeUser", {
        userId: selectedRemoveUser
      });

      setSelectedRemoveUser("");

      // Reload users
      const usersRes = await axios.get("http://localhost:5000/api/users");
      setUsers(usersRes.data.filter(u => u.organization_id === user.organization_id));
      setAvailableUsers(usersRes.data.filter(u => !u.organization_id));
    } catch (err) {
      console.error(err);
      alert("Failed to remove user");
    }
  };

  // Table renderer
  const renderTable = (title, data, columns) => (
    <div className="table-section">
      <h2>{title}</h2>
      <table className="custom-table">
        <thead>
          <tr>
            {columns.map(col => <th key={col}>{col}</th>)}
          </tr>
        </thead>
        <tbody>
          {data.map((row, idx) => (
            <tr key={idx}>
              {columns.map(col =>
                col === "No" ? <td key={col}>{idx + 1}</td> :
                <td key={col}>{row[col.toLowerCase()] ?? "N/A"}</td>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  return (
    <div className="organization-container">
      <button className="back-button" onClick={() => navigate(-1)}>Back</button>
      <h1 className="org-title">Organization: {organizationName}</h1>

      {/* Users Table */}
      {renderTable("Users", users, ["No", "Username", "Points", "Tournaments_played", "Role"])}

      {/* Admin controls */}
      {isAdmin && (
        <div className="org-controls">
          <div className="control-section">
            <h3>Add User</h3>
            <select value={selectedAddUser} onChange={(e) => setSelectedAddUser(e.target.value)}>
              <option value="">Select a user</option>
              {availableUsers.map(u => (
                <option key={u.id} value={u.id}>{u.username}</option>
              ))}
            </select>
            <button onClick={handleAddUser}>Add</button>
          </div>

          <div className="control-section">
            <h3>Remove User</h3>
            <select value={selectedRemoveUser} onChange={(e) => setSelectedRemoveUser(e.target.value)}>
              <option value="">Select a user</option>
              {users.map(u => (
                <option key={u.id} value={u.id}>{u.username}</option>
              ))}
            </select>
            <button onClick={handleRemoveUser}>Remove</button>
          </div>
        </div>
      )}

      {/* Tournaments Table */}
      {renderTable("Tournaments", tournaments, ["No", "Time", "Date", "Max_players"])}
    </div>
  );
}

export default OrganizationPage;
