import React, { useEffect, useState } from "react";
import axios from "axios";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
  Link
} from "react-router-dom";

import LoginPage from "./pages/LoginPage";
import RegisterPage from "./pages/RegisterPage";
import ProfilePage from "./pages/ProfilePage";
import OrganizationPage from "./pages/OrganizationPage";

import "./App.css";

// ---------------------------
// MOCK AUTH (replace later)
// ---------------------------
const useAuth = () => {
  const [user, setUser] = useState(() => {
    const stored = localStorage.getItem("user");
    return stored ? JSON.parse(stored) : null;
  });

  const login = (userData) => {
    localStorage.setItem("user", JSON.stringify(userData));
    setUser(userData);
  };

  const logout = () => {
    localStorage.removeItem("user");
    setUser(null);
  };

  return { user, login, logout };
};

// ---------------------------
// MAIN DASHBOARD PAGE
// ---------------------------
function Dashboard({ currentUser }) {
  const [users, setUsers] = useState([]);
  const [matches, setMatches] = useState([]);
  const [tournaments, setTournaments] = useState([]);

  useEffect(() => {
    const load = async () => {
      try {
        const [u, m, t] = await Promise.all([
          axios.get("http://localhost:5000/api/users"),
          axios.get("http://localhost:5000/api/matches"),
          axios.get("http://localhost:5000/api/tournaments"),
        ]);
        setUsers(u.data);
        setMatches(m.data);
        setTournaments(t.data);
      } catch (err) {
        console.error(err);
      }
    };
    load();
  }, []);

  const renderTable = (title, data, columns) => (
    <div className="table-section">
      <h2>{title}</h2>
      <table className="custom-table">
        <thead>
          <tr>
            {columns.map((col) => (
              <th key={col}>{col}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map((row, index) => (
            <tr key={index}>
              {columns.map((col) =>
                col === "No" ? (
                  <td key={col}>{index + 1}</td>
                ) : (
                  <td key={col}>{row[col.toLowerCase()] ?? "N/A"}</td>
                )
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  const globalLeaderboard = users;
  const orgLeaderboard = users.filter(
    (u) => u.organization === currentUser.organization
  );
  const friendsLeaderboard = users.filter((u) =>
    currentUser?.friends?.includes(u.username)
  );

  const lastFive = matches
    .filter((m) => m.user_id === currentUser.id)
    .slice(0, 5);

  const orgTournaments = tournaments
    .filter((t) => t.organization_id === currentUser.organization_id)
    .slice(0, 5);

  return (
    <div className="page-container">
      {/* USER BAR */}
      <div className="user-bar">
        <span>User: {currentUser.username}</span>
        <span>Points: {currentUser.points}</span>
        <span>Org: {currentUser.organization}</span>

        <Link to="/profile">
          <button className="small-button">Profile</button>
        </Link>

        <Link to="/organization">
          <button className="small-button">Organization</button>
        </Link>
      </div>

      <div className="dashboard-row">
        <div className="tables-wrapper">
          {renderTable("Global Leaderboard", globalLeaderboard, [
            "No",
            "Username",
            "Points",
            "Organization",
          ])}
          {renderTable("Organization Leaderboard", orgLeaderboard, [
            "No",
            "Username",
            "Points",
            "Organization",
          ])}
          {renderTable("Friends Leaderboard", friendsLeaderboard, [
            "No",
            "Username",
            "Points",
            "Organization",
          ])}
        </div>

        <div className="matches-wrapper">
          {renderTable("Last 5 Matches", lastFive, [
            "No",
            "Points",
            "Score",
            "Match_date",
          ])}

          {renderTable("Organization Tournaments", orgTournaments, [
            "No",
            "Time",
            "Date",
            "Max_players",
          ])}
        </div>
      </div>
    </div>
  );
}

// ---------------------------------------
// MAIN APP WITH ROUTING
// ---------------------------------------
function App() {
  const { user } = useAuth();

  return (
    <Router>
      <Routes>
        {/* Login/Register */}
        <Route path="/login" element={<LoginPage />} />
        <Route path="/register" element={<RegisterPage />} />

        {/* Protected Routes */}
        <Route
          path="/"
          element={
            user ? (
              <Dashboard currentUser={user} />
            ) : (
              <Navigate to="/login" />
            )
          }
        />

        <Route
          path="/profile"
          element={user ? <ProfilePage /> : <Navigate to="/login" />}
        />

        <Route
          path="/organization"
          element={user ? <OrganizationPage /> : <Navigate to="/login" />}
        />
      </Routes>
    </Router>
  );
}

export default App;
