import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../App.css";

function OrganizationPage({ user }) {
  const navigate = useNavigate();

  const [users, setUsers] = useState([]);
  const [allUsers, setAllUsers] = useState([]);
  const [tournaments, setTournaments] = useState([]);
  const [organizationName, setOrganizationName] = useState("");

  const [selectedAddUser, setSelectedAddUser] = useState("");
  const [selectedRemoveUser, setSelectedRemoveUser] = useState("");

  // Load data on mount
  useEffect(() => {
    const loadData = async () => {
      try {
        const [usersRes, tournamentsRes, orgRes] = await Promise.all([
          axios.get("http://localhost:5000/api/users"),
          axios.get("http://localhost:5000/api/tournaments"),
          axios.get("http://localhost:5000/api/organizations")
        ]);

        // All users (needed for adding)
        setAllUsers(usersRes.data);

        // Filter organization users
        const filteredUsers = usersRes.data.filter(
          (u) => u.organization_id === user.organization_id
        );

        // Filter tournaments
        const filteredTournaments = tournamentsRes.data.filter(
          (t) => t.organization_id === user.organization_id
        );

        // Organization name
        const org = orgRes.data.find(
          (o) => o.id === user.organization_id
        );

        setUsers(filteredUsers);
        setTournaments(filteredTournaments);
        setOrganizationName(org ? org.name : "Unknown");
      } catch (err) {
        console.error(err);
      }
    };

    loadData();
  }, [user.organization_id]);

  // -------------------------------------
  // ADD USER TO ORGANIZATION
  // -------------------------------------
  const handleAddUser = async () => {
    if (!selectedAddUser) return;

    await axios.put(`http://localhost:5000/api/org/addUser`, {
      userId: selectedAddUser,
      orgId: user.organization_id
    });

    window.location.reload();
  };

  // -------------------------------------
  // REMOVE USER FROM ORGANIZATION
  // -------------------------------------
  const handleRemoveUser = async () => {
    if (!selectedRemoveUser) return;

    await axios.put(`http://localhost:5000/api/org/removeUser`, {
      userId: selectedRemoveUser
    });

    window.location.reload();
  };

  // Users NOT in organization (for ADD dropdown)
  const addableUsers = allUsers.filter(
    (u) => u.organization_id !== user.organization_id
  );

  return (
    <div className="organization-container">

      {/* Back button */}
      <button className="back-button" onClick={() => navigate(-1)}>
        Back
      </button>

      <h1 className="org-title">Organization: {organizationName}</h1>

      {/* ADMIN MANAGEMENT TOOLS */}
      {user.role === "admin" && (
        <div className="admin-controls">
          {/* ADD USER SECTION */}
          <div className="admin-box">
            <h3>Add User to Organization</h3>

            <select
              className="dropdown"
              value={selectedAddUser}
              onChange={(e) => setSelectedAddUser(e.target.value)}
            >
              <option value="">Select a user</option>
              {addableUsers.map((u) => (
                <option key={u.id} value={u.id}>
                  {u.username}
                </option>
              ))}
            </select>

            <button className="add-button" onClick={handleAddUser}>
              Add User
            </button>
          </div>

          {/* REMOVE USER SECTION */}
          <div className="admin-box">
            <h3>Remove User from Organization</h3>

            <select
              className="dropdown"
              value={selectedRemoveUser}
              onChange={(e) => setSelectedRemoveUser(e.target.value)}
            >
              <option value="">Select a user</option>
              {users.map((u) => (
                <option key={u.id} value={u.id}>
                  {u.username}
                </option>
              ))}
            </select>

            <button className="remove-button" onClick={handleRemoveUser}>
              Remove User
            </button>
          </div>
        </div>
      )}

      {/* USERS TABLE */}
      <div className="table-section">
        <h2>Users</h2>

        <table className="custom-table">
          <thead>
            <tr>
              <th>No</th>
              <th>Username</th>
              <th>Points</th>
              <th>Role</th>
            </tr>
          </thead>

          <tbody>
            {users.map((u, index) => (
              <tr key={u.id}>
                <td>{index + 1}</td>
                <td>{u.username}</td>
                <td>{u.points}</td>
                <td>{u.role}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* TOURNAMENTS TABLE */}
      <div className="table-section">
        <h2>Tournaments</h2>

        <table className="custom-table">
          <thead>
            <tr>
              <th>No</th>
              <th>Time</th>
              <th>Date</th>
              <th>Max Players</th>
            </tr>
          </thead>

          <tbody>
            {tournaments.map((t, index) => (
              <tr key={t.id}>
                <td>{index + 1}</td>
                <td>{t.time}</td>
                <td>{t.date}</td>
                <td>{t.max_players}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

    </div>
  );
}

export default OrganizationPage;
