import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../App.css";

function OrganizationPage({ user }) {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [tournaments, setTournaments] = useState([]);
  const [organizationName, setOrganizationName] = useState("");
  const [allUsers, setAllUsers] = useState([]); // users not in any org
  const [selectedAddUser, setSelectedAddUser] = useState("");
  const [selectedRemoveUser, setSelectedRemoveUser] = useState("");

  const isAdmin = user.role === "admin";

  useEffect(() => {
    const loadData = async () => {
      try {
        const [usersRes, tournamentsRes, orgRes, allUsersRes] = await Promise.all([
          axios.get("http://localhost:5000/api/users"),
          axios.get("http://localhost:5000/api/tournaments"),
          axios.get("http://localhost:5000/api/organizations"),
          axios.get("http://localhost:5000/api/users") // we will filter for no org
        ]);

        // Filter users for current org
        const filteredUsers = usersRes.data.filter(
          (u) => u.organization_id === user.organization_id
        );

        // Filter tournaments for current org
        const filteredTournaments = tournamentsRes.data.filter(
          (t) => t.organization_id === user.organization_id
        );

        // Organization name
        const org = orgRes.data.find((o) => o.id === user.organization_id);

        // Users not in any organization
        const availableUsers = allUsersRes.data.filter((u) => !u.organization_id);

        setUsers(filteredUsers);
        setTournaments(filteredTournaments);
        setOrganizationName(org ? org.name : "Unknown");
        setAllUsers(availableUsers);
      } catch (err) {
        console.error(err);
      }
    };

    loadData();
  }, [user.organization_id]);

  const handleAddUser = async () => {
    if (!selectedAddUser) return;
    try {
      await axios.put(`http://localhost:5000/api/users/${selectedAddUser}/assign`, {
        organization_id: user.organization_id
      });
      setSelectedAddUser("");
      // Reload users
      const usersRes = await axios.get("http://localhost:5000/api/users");
      setUsers(usersRes.data.filter((u) => u.organization_id === user.organization_id));
      setAllUsers(usersRes.data.filter((u) => !u.organization_id));
    } catch (err) {
      console.error(err);
      alert("Failed to add user");
    }
  };

  const handleRemoveUser = async () => {
    if (!selectedRemoveUser) return;
    try {
      await axios.put(`http://localhost:5000/api/users/${selectedRemoveUser}/remove`);
      setSelectedRemoveUser("");
      const usersRes = await axios.get("http://localhost:5000/api/users");
      setUsers(usersRes.data.filter((u) => u.organization_id === user.organization_id));
      setAllUsers(usersRes.data.filter((u) => !u.organization_id));
    } catch (err) {
      console.error(err);
      alert("Failed to remove user");
    }
  };

  const renderTable = (title, data, columns) => (
    <div className="table-section">
      <h2>{title}</h2>
      <table className="custom-table">
        <thead>
          <tr>
            {columns.map((col) => (<th key={col}>{col}</th>))}
          </tr>
        </thead>
        <tbody>
          {data.map((row, index) => (
            <tr key={index}>
              {columns.map((col) =>
                col === "No" ? <td key={col}>{index + 1}</td> :
                <td key={col}>{row[col.toLowerCase()] ?? "N/A"}</td>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  return (
    <div className="organization-container">
      <button className="back-button" onClick={() => navigate(-1)}>
        Back
      </button>

      <h1 className="org-title">Organization: {organizationName}</h1>

      {/* Users Section */}
      {renderTable("Users", users, ["No", "Username", "Points", "Tournaments_played", "Role"])}

      {isAdmin && (
        <div className="org-controls">
          <div className="control-section">
            <h3>Add User</h3>
            <select
              value={selectedAddUser}
              onChange={(e) => setSelectedAddUser(e.target.value)}
            >
              <option value="">Select a user</option>
              {allUsers.map((u) => (
                <option key={u.id} value={u.id}>
                {u.username}
              </option>
              ))}
            </select>
            <button onClick={handleAddUser}>Add</button>
          </div>

          <div className="control-section">
            <h3>Remove User</h3>
            <select
              value={selectedRemoveUser}
              onChange={(e) => setSelectedRemoveUser(e.target.value)}
            >
              <option value="">Select a user</option>
              {users.map((u) => (
                <option key={u.id} value={u.id}>
                {u.username}
              </option> 
              ))}
            </select>
            <button onClick={handleRemoveUser}>Remove</button>
          </div>
        </div>
      )}

      {/* Tournaments Section */}
      {renderTable("Tournaments", tournaments, ["No", "Time", "Date", "Max_players"])}
    </div>
  );
}

export default OrganizationPage;
