import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../App.css";

function OrganizationPage({ user }) {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [tournaments, setTournaments] = useState([]);
  const [organizationName, setOrganizationName] = useState("");

  useEffect(() => {
    const loadData = async () => {
      try {
        const [usersRes, tournamentsRes, orgRes] = await Promise.all([
          axios.get("http://localhost:5000/api/users"),
          axios.get("http://localhost:5000/api/tournaments"),
          axios.get("http://localhost:5000/api/organizations")
        ]);

        const filteredUsers = usersRes.data.filter(
          (u) => u.organization_id === user.organization_id
        );

        const filteredTournaments = tournamentsRes.data.filter(
          (t) => t.organization_id === user.organization_id
        );

        const org = orgRes.data.find(
          (o) => o.id === user.organization_id
        );

        setUsers(filteredUsers);
        setTournaments(filteredTournaments);
        setOrganizationName(org ? org.name : "Unknown");
      } catch (err) {
        console.error(err);
      }
    };

    loadData();
  }, [user.organization_id]);

  // RENDER TABLE
  const renderTable = (title, data, columns, enableAdd) => (
    <div className="table-section">
      <h2>{title}</h2>

      {/* ADMIN ONLY BUTTON */}
      {user.role === "admin" && enableAdd && (
        <button className="add-button" onClick={() => alert("Add action coming soon…")}>
          Add
        </button>
      )}

      <table className="custom-table">
        <thead>
          <tr>
            {columns.map((col) => (
              <th key={col}>{col}</th>
            ))}
          </tr>
        </thead>

        <tbody>
          {data.map((row, index) => (
            <tr key={index}>
              {columns.map((col) =>
                col === "No" ? (
                  <td key={col}>{index + 1}</td>
                ) : (
                  <td key={col}>
                    {row[col.toLowerCase()] ?? "N/A"}
                  </td>
                )
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  return (
    <div className="organization-container">
      <button className="back-button" onClick={() => navigate("/dashboard")}>
        Back
      </button>

      <h1 className="org-title">Organization: {organizationName}</h1>

      {renderTable(
        "Users",
        users,
        ["No", "Username", "Points", "Tournaments_played", "Role"],
        true // admins can add users
      )}

      {renderTable(
        "Tournaments",
        tournaments,
        ["No", "Time", "Date", "Max_players"],
        true // admins can add tournaments
      )}
    </div>
  );
}

export default OrganizationPage;
