import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../App.css";

function ProfilePage({ user, setUser }) {
  const navigate = useNavigate();

  const [friends, setFriends] = useState(user.friends || []);
  const [allUsers, setAllUsers] = useState([]);
  const [selectedAddFriend, setSelectedAddFriend] = useState("");
  const [selectedRemoveFriend, setSelectedRemoveFriend] = useState("");

  // Load all users
  useEffect(() => {
    const loadUsers = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/users");
        setAllUsers(res.data.filter(u => u.id !== user.id)); // exclude self
      } catch (err) {
        console.error(err);
      }
    };
    loadUsers();
  }, [user.id]);

  // Add friend
  const handleAddFriend = () => {
    if (!selectedAddFriend) return;
    if (!friends.includes(selectedAddFriend)) {
      const updatedFriends = [...friends, selectedAddFriend];
      setFriends(updatedFriends);
      setSelectedAddFriend("");
      // Update backend (optional)
      axios.put(`http://localhost:5000/api/users/${user.id}/friends`, { friends: updatedFriends })
        .then(res => {
          setUser(prev => ({ ...prev, friends: updatedFriends }));
        })
        .catch(err => console.error(err));
    }
  };

  // Remove friend
  const handleRemoveFriend = () => {
    if (!selectedRemoveFriend) return;
    const updatedFriends = friends.filter(f => f !== selectedRemoveFriend);
    setFriends(updatedFriends);
    setSelectedRemoveFriend("");
    // Update backend (optional)
    axios.put(`http://localhost:5000/api/users/${user.id}/friends`, { friends: updatedFriends })
      .then(res => {
        setUser(prev => ({ ...prev, friends: updatedFriends }));
      })
      .catch(err => console.error(err));
  };

  // Compute dropdown options
  const addFriendOptions = allUsers
    .filter(u => !friends.includes(u.username))
    .map(u => ({ id: u.id, username: u.username }));

  const removeFriendOptions = allUsers
    .filter(u => friends.includes(u.username))
    .map(u => ({ id: u.id, username: u.username }));

  return (
    <div className="profile-container">
      <button className="back-button" onClick={() => navigate(-1)}>
        Back
      </button>

      <h1>Profile: {user.username}</h1>
      <p>Email: {user.email}</p>
      <p>Points: {user.points}</p>

      {/* Friends Management */}
      <div className="friend-management">
        <div className="control-section">
          <h3>Add Friend</h3>
          <select
            value={selectedAddFriend}
            onChange={(e) => setSelectedAddFriend(e.target.value)}
          >
            <option value="">Select a user</option>
            {addFriendOptions.map(u => (
              <option key={u.id} value={u.username}>{u.username}</option>
            ))}
          </select>
          <button onClick={handleAddFriend}>Add</button>
        </div>

        <div className="control-section">
          <h3>Remove Friend</h3>
          <select
            value={selectedRemoveFriend}
            onChange={(e) => setSelectedRemoveFriend(e.target.value)}
          >
            <option value="">Select a friend</option>
            {removeFriendOptions.map(u => (
              <option key={u.id} value={u.username}>{u.username}</option>
            ))}
          </select>
          <button onClick={handleRemoveFriend}>Remove</button>
        </div>
      </div>

      {/* Friends List */}
      <div className="friends-list">
        <h2>Friends:</h2>
        <ul>
          {friends.length > 0 ? friends.map((f, idx) => (
            <li key={idx}>{f}</li>
          )) : <li>No friends yet</li>}
        </ul>
      </div>
    </div>
  );
}

export default ProfilePage;
