import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./App.css";

function OrganizationPage() {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [tournaments, setTournaments] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const usersRes = await axios.get("http://localhost:5000/api/users");
        setUsers(usersRes.data);
        const tournamentsRes = await axios.get("http://localhost:5000/api/tournaments");
        setTournaments(tournamentsRes.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchData();
  }, []);

  return (
    <div className="page-container">
      <h1 className="page-title">Organization Page</h1>

      <button onClick={() => navigate("/")} className="small-button">
        Back
      </button>

      <div className="table-section">
        <button className="add-button">Add User</button>
        <table className="custom-table">
          <thead>
            <tr>
              <th>No</th>
              <th>Username</th>
              <th>Points</th>
              <th>Tournaments Played</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user, idx) => (
              <tr key={idx}>
                <td>{idx + 1}</td>
                <td>{user.username}</td>
                <td>{user.points}</td>
                <td>{user.tournaments_played || 0}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="table-section">
        <button className="add-button">Add Tournament</button>
        <table className="custom-table">
          <thead>
            <tr>
              <th>No</th>
              <th>Time</th>
              <th>Date</th>
              <th>Max Players</th>
            </tr>
          </thead>
          <tbody>
            {tournaments.map((t, idx) => (
              <tr key={idx}>
                <td>{idx + 1}</td>
                <td>{t.time}</td>
                <td>{t.date}</td>
                <td>{t.max_players}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default OrganizationPage;
