import React, { useEffect, useState } from "react";
import axios from "axios";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import ProfilePage from "./pages/ProfilePage";
import OrganizationPage from "./pages/OrganizationPage";
import "./App.css";

function App() {
  const [users, setUsers] = useState([]);
  const [matches, setMatches] = useState([]);
  const [tournaments, setTournaments] = useState([]);

  // Mock current user (replace with API later if needed)
  const currentUser = { id: 1, username: "Alice", points: 3000, organization: "Org X" };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const usersRes = await axios.get("http://localhost:5000/api/users");
        setUsers(usersRes.data);

        const matchesRes = await axios.get("http://localhost:5000/api/matches");
        setMatches(matchesRes.data);

        const tournamentsRes = await axios.get("http://localhost:5000/api/tournaments");
        setTournaments(tournamentsRes.data);
      } catch (err) {
        console.error(err);
      }
    };

    fetchData();
  }, []);

  // Helper to render tables
  const renderTable = (title, data, columns) => (
    <div className="table-section">
      <h2>{title}</h2>
      <table className="custom-table">
        <thead>
          <tr>
            {columns.map((col) => (
              <th key={col}>{col}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map((row, idx) => (
            <tr key={idx}>
              {columns.map((col) => (
                <td key={col}>{row[col.toLowerCase()] ?? row[col]}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={
            <div className="page-container">
              {/* User Bar */}
              <div className="user-bar">
                <span>User: {currentUser.username}</span>
                <span>Points: {currentUser.points}</span>
                <span>Organization: {currentUser.organization}</span>
                <Link to="/profile">
                  <button className="small-button">Profile</button>
                </Link>
                <Link to="/organization">
                  <button className="small-button">Organization</button>
                </Link>
              </div>

              <div className="dashboard-row">
                {/* Left side: Leaderboards */}
                <div className="tables-wrapper">
                  {renderTable("Global Leaderboard", users, ["No", "Username", "Points", "Organization"])}
                  {/* You can filter users by organization or friends for other tables */}
                </div>

                {/* Right side: Last 5 matches */}
                <div className="matches-wrapper">
                  {renderTable(
                    "Last 5 Matches",
                    matches.slice(0, 5), // only last 5
                    ["No", "Points", "Score", "Match_date"]
                  )}
                  {renderTable(
                    "Organization Tournaments",
                    tournaments.slice(0, 5),
                    ["No", "Time", "Date", "Max_players"]
                  )}
                </div>
              </div>
            </div>
          }
        />
        <Route path="/profile" element={<ProfilePage />} />
        <Route path="/organization" element={<OrganizationPage />} />
      </Routes>
    </Router>
  );
}

export default App;
