import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../App.css";

function NewGamePage({ user }) {
  const navigate = useNavigate();
  const [allUsers, setAllUsers] = useState([]);
  const [numPlayers, setNumPlayers] = useState(2);
  const [selectedUsers, setSelectedUsers] = useState([user]); // first user is always logged-in
  const [userPoints, setUserPoints] = useState({ [user.id]: 0 });

  useEffect(() => {
    const loadUsers = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/users");
        const otherUsers = res.data.filter(u => u.id !== user.id);
        setAllUsers(otherUsers);
      } catch (err) {
        console.error(err);
      }
    };
    loadUsers();
  }, [user]);

  const handleNumPlayersChange = (e) => {
    const n = parseInt(e.target.value);
    setNumPlayers(n);
    setSelectedUsers(prev => [prev[0]]); // reset to only logged user
    setUserPoints({ [user.id]: 0 });
  };

  const handleSelectUser = (index, userId) => {
    const userObj = allUsers.find(u => u.id === parseInt(userId));
    setSelectedUsers(prev => {
      const updated = [...prev];
      updated[index] = userObj;
      return updated;
    });
    setUserPoints(prev => ({ ...prev, [userId]: 0 }));
  };

  const handlePointChange = (userId, value) => {
    const num = parseInt(value);
    if (num < 2) return;
    setUserPoints(prev => ({ ...prev, [userId]: num }));
  };

  const handleAddGame = async () => {
    try {
      for (let u of selectedUsers) {
        const pts = userPoints[u.id];
        let delta = 0;
        if ([1,2].includes(pts)) delta = -3;
        else if ([3,4].includes(pts)) delta = -1;
        else if ([5,6,7].includes(pts)) delta = 0;
        else if ([8,9].includes(pts)) delta = 2;
        else if (pts === 10) delta = 5;

        await axios.put("http://localhost:5000/api/users/updatePoints", {
          userId: u.id,
          delta
        });
      }

      alert("Game added and points updated!");
      navigate("/dashboard"); // go back to main page
    } catch (err) {
      console.error(err);
      alert("Failed to add game");
    }
  };

  return (
    <div className="newgame-container">
      <button className="back-button" onClick={() => navigate("/dashboard")}>Back</button>
      <h1>New Game</h1>

      <div className="control-section">
        <label>Number of Players: </label>
        <select value={numPlayers} onChange={handleNumPlayersChange}>
          {[2,3,4].map(n => <option key={n} value={n}>{n}</option>)}
        </select>
      </div>

      <table className="custom-table">
        <thead>
          <tr>
            <th>User</th>
            <th>Points</th>
          </tr>
        </thead>
        <tbody>
            {Array.from({ length: numPlayers }).map((_, index) => {
                // For first user, always logged-in user
                const currentUser = index === 0 ? user : selectedUsers[index];

                return (
                <tr key={index}>
                    <td>
                    {index === 0 ? (
                        user.username
                    ) : (
                        <select
                        value={currentUser?.id || ""}
                        onChange={e => handleSelectUser(index, e.target.value)}
                        >
                        <option value="">Select user</option>
                        {allUsers
                            .filter(
                            u => !selectedUsers.slice(1).some(su => su?.id === u.id)
                            )
                            .map(u => (
                            <option key={u.id} value={u.id}>{u.username}</option>
                            ))}
                        </select>
                    )}
                    </td>
                    <td>
                    <input
                        type="number"
                        min="1"
                        max="10"
                        value={userPoints[currentUser?.id] || ""}
                        onChange={e =>
                        setUserPoints(prev => ({
                            ...prev,
                            [currentUser.id]: e.target.value === "" ? "" : parseInt(e.target.value)
                        }))
                        }
                    />
                    </td>
                </tr>
                );
            })}
            </tbody>
      </table>
      <button onClick={handleAddGame} className="add-game-button">Add Game</button>
    </div>
  );
}

export default NewGamePage;
