import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "a./App.css";

function OrganizationPage() {
  const navigate = useNavigate();
  const [users, setUsers] = useState([]);
  const [tournaments, setTournaments] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [usersRes, tournamentsRes] = await Promise.all([
          axios.get("http://localhost:5000/api/users"),
          axios.get("http://localhost:5000/api/tournaments")
        ]);
        setUsers(usersRes.data);
        setTournaments(tournamentsRes.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchData();
  }, []);

  const renderTable = (title, data, columns) => (
    <div className="table-section">
      <h2>{title}</h2>
      <table className="custom-table">
        <thead>
          <tr>
            {columns.map(col => <th key={col}>{col}</th>)}
          </tr>
        </thead>
        <tbody>
          {data.map((row, index) => (
            <tr key={index}>
              {columns.map(col =>
                col === "No" ? <td key={col}>{index + 1}</td> :
                <td key={col}>{row[col.toLowerCase()] ?? "N/A"}</td>
              )}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  return (
    <div className="page-container">
      <h1 className="page-title">Organization Page</h1>

      <button onClick={() => navigate("/")} className="small-button" style={{ marginBottom: "20px" }}>
        Back
      </button>

      <button className="add-button">Add User</button>
      {renderTable("Users", users, ["No", "Username", "Points", "Tournaments_played"])}

      <button className="add-button">Add Tournament</button>
      {renderTable("Tournaments", tournaments, ["No", "Time", "Date", "Max_players"])}
    </div>
  );
}

export default OrganizationPage;
