import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useNavigate
} from "react-router-dom";

import ProfilePage from "./pages/ProfilePage";
import OrganizationPage from "./pages/OrganizationPage";
import NewGamePage from "./pages/NewGamePage";
import "./App.css";

/* ===========================
   LOGIN
=========================== */
function LoginPage({ setUser }) {
  const navigate = useNavigate();
  const [form, setForm] = useState({ email: "", password: "" });
  const [error, setError] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");
    try {
      const res = await axios.post("http://localhost:5000/api/auth/login", form);
      setUser(res.data.user);
      localStorage.setItem("user", JSON.stringify(res.data.user));
      navigate("/dashboard");
    } catch (err) {
      setError(err.response?.data?.error || "Login failed");
    }
  };

  return (
    <div className="auth-container">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <input
          type="email"
          placeholder="Email"
          value={form.email}
          onChange={(e) => setForm({ ...form, email: e.target.value })}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={form.password}
          onChange={(e) => setForm({ ...form, password: e.target.value })}
          required
        />
        <button type="submit">Login</button>
        {error && <p className="error">{error}</p>}
      </form>
      <button onClick={() => navigate("/register")}>Create account</button>
    </div>
  );
}

/* ===========================
   REGISTER
=========================== */
function RegisterPage() {
  const navigate = useNavigate();
  const [form, setForm] = useState({ username: "", email: "", password: "" });
  const [error, setError] = useState("");

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/auth/register", form);
      navigate("/");
    } catch (err) {
      setError(err.response?.data?.error || "Registration failed");
    }
  };

  return (
    <div className="auth-container">
      <h2>Register</h2>
      <form onSubmit={handleRegister}>
        <input
          placeholder="Username"
          value={form.username}
          onChange={(e) => setForm({ ...form, username: e.target.value })}
          required
        />
        <input
          placeholder="Email"
          value={form.email}
          onChange={(e) => setForm({ ...form, email: e.target.value })}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={form.password}
          onChange={(e) => setForm({ ...form, password: e.target.value })}
          required
        />
        <button type="submit">Register</button>
        {error && <p className="error">{error}</p>}
      </form>
    </div>
  );
}

/* ===========================
   DASHBOARD
=========================== */
function Dashboard({ user, setUser }) {
  const navigate = useNavigate();

  const [users, setUsers] = useState([]);
  const [friends, setFriends] = useState([]);
  const [tournaments, setTournaments] = useState([]);
  const [lastMatches, setLastMatches] = useState([]);

  useEffect(() => {
    const load = async () => {
      const [usersRes, friendsRes, tournamentsRes, matchesRes] =
        await Promise.all([
          axios.get("http://localhost:5000/api/users"),
          axios.get(`http://localhost:5000/api/friends/${user.id}`),
          axios.get("http://localhost:5000/api/tournaments"),
          axios.get(`http://localhost:5000/api/matches/${user.id}`)
        ]);

      setUsers(usersRes.data);
      setFriends(friendsRes.data.map(f => f.username));
      setTournaments(tournamentsRes.data);
      setLastMatches(matchesRes.data);
    };

    load();
  }, [user.id]);

  const logout = () => {
    setUser(null);
    localStorage.removeItem("user");
    navigate("/");
  };

  /* ---------- TABLE RENDER ---------- */
  const renderTable = (title, data, columns) => (
    <div className="table-section">
      <h2>{title}</h2>
      <table>
        <thead>
          <tr>
            {columns.map(c => <th key={c.label}>{c.label}</th>)}
          </tr>
        </thead>
        <tbody>
          {data.length === 0 ? (
            <tr><td colSpan={columns.length}>No data</td></tr>
          ) : (
            data.map((row, i) => (
              <tr key={i}>
                {columns.map(c => (
                  <td key={c.label}>
                    {c.key === "index"
                      ? i + 1
                      : c.key === "username"
                        ? user.username
                        : row[c.key]}
                  </td>
                ))}
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );

  /* ---------- FILTERS ---------- */
  const orgUsers = users.filter(u => u.organization_id === user.organization_id);
  const friendsUsers = users.filter(u => friends.includes(u.username));
  const orgTournaments = tournaments.filter(
    t => t.organization_id === user.organization_id
  ).slice(0, 5);

  return (
    <div className="dashboard-container">
      <div className="left-panel">
        <p>User: {user.username}</p>
        <p>Points: {user.points}</p>
        <button onClick={() => navigate("/profile")}>Profile</button>
        <button onClick={() => navigate("/organization")}>Organization</button>
        <button onClick={logout}>Logout</button>
        <button onClick={() => navigate("/new-game")}>New Game</button>
      </div>

      <div className="right-panel">
        {renderTable("Global Leaderboard", users, [
          { label: "No", key: "index" },
          { label: "Username", key: "username" },
          { label: "Points", key: "points" },
          { label: "Organization", key: "organization" }
        ])}

        {renderTable("Organization Leaderboard", orgUsers, [
          { label: "No", key: "index" },
          { label: "Username", key: "username" },
          { label: "Points", key: "points" }
        ])}

        {renderTable("Friends Leaderboard", friendsUsers, [
          { label: "No", key: "index" },
          { label: "Username", key: "username" },
          { label: "Points", key: "points" }
        ])}

        {renderTable("Last 5 Matches", lastMatches, [
          { label: "No", key: "index" },
          { label: "Username", key: "username" },
          { label: "Score", key: "score" },
          { label: "Points Change", key: "points_change" },
          { label: "Date", key: "match_date" }
        ])}

        {renderTable("Organization Tournaments", orgTournaments, [
          { label: "No", key: "index" },
          { label: "Time", key: "time" },
          { label: "Date", key: "date" },
          { label: "Max Players", key: "max_players" }
        ])}
      </div>
    </div>
  );
}

/* ===========================
   APP
=========================== */
export default function App() {
  const [user, setUser] = useState(() =>
    JSON.parse(localStorage.getItem("user"))
  );

  return (
    <Router>
      <Routes>
        <Route path="/" element={<LoginPage setUser={setUser} />} />
        <Route path="/register" element={<RegisterPage />} />
        <Route
          path="/dashboard"
          element={user ? <Dashboard user={user} setUser={setUser} /> : <LoginPage setUser={setUser} />}
        />
        <Route path="/profile" element={<ProfilePage user={user} />} />
        <Route path="/organization" element={<OrganizationPage user={user} />} />
        <Route path="/new-game" element={<NewGamePage user={user} />} />
      </Routes>
    </Router>
  );
}
