import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../App.css";

function NewGamePage({ user }) {
  const navigate = useNavigate();

  const [numPlayers, setNumPlayers] = useState(2);
  const [selectedUsers, setSelectedUsers] = useState([]);
  const [userPoints, setUserPoints] = useState([]);
  const [allUsers, setAllUsers] = useState([]);

  useEffect(() => {
    const loadUsers = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/users");
        // Exclude the logged-in user from the other users
        setAllUsers(res.data.filter(u => u.id !== user.id));
        setSelectedUsers([user.id]); // first user is always logged-in user
        setUserPoints([0]); // initialize logged-in user's points
      } catch (err) {
        console.error(err);
      }
    };
    loadUsers();
  }, [user]);

  const handleNumPlayersChange = (e) => {
    const num = parseInt(e.target.value);
    setNumPlayers(num);

    // Adjust selectedUsers and userPoints length
    setSelectedUsers(prev => {
      const newArr = prev.slice(0, num);
      while (newArr.length < num) newArr.push(null);
      return newArr;
    });
    setUserPoints(prev => {
      const newArr = prev.slice(0, num);
      while (newArr.length < num) newArr.push(0);
      return newArr;
    });
  };

  const handleUserChange = (index, userId) => {
    const newSelected = [...selectedUsers];
    newSelected[index] = parseInt(userId);
    setSelectedUsers(newSelected);
  };

  const handlePointsChange = (index, points) => {
    const newPoints = [...userPoints];
    newPoints[index] = parseInt(points);
    setUserPoints(newPoints);
  };

  const handleAddGame = async () => {
    try {
      // Prepare points update based on rules
      const pointsUpdate = selectedUsers.map((uId, i) => {
        const pts = userPoints[i];
        let delta = 0;
        if (pts >= 1 && pts <= 2) delta = -3;
        else if (pts >= 3 && pts <= 4) delta = -1;
        else if (pts >= 5 && pts <= 7) delta = 0;
        else if (pts >= 8 && pts <= 9) delta = 2;
        else if (pts === 10) delta = 5;
        return { userId: uId, delta };
      });

      await axios.post("http://localhost:5000/api/games/add", { pointsUpdate });

      // After adding game, go back to main page
      navigate("/dashboard");
    } catch (err) {
      console.error(err);
      alert("Failed to add game");
    }
  };

  return (
    <div className="new-game-page">
      {/* Back button */}
      <button className="back-button" onClick={() => navigate("/dashboard")}>
        Back
      </button>

      <h1>New Game</h1>

      <div className="form-section">
        <label>Number of Players: </label>
        <select value={numPlayers} onChange={handleNumPlayersChange}>
          {[2, 3, 4].map(n => <option key={n} value={n}>{n}</option>)}
        </select>
      </div>

      <table className="custom-table">
        <thead>
          <tr>
            <th>Username</th>
            <th>Points</th>
          </tr>
        </thead>
        <tbody>
          {Array.from({ length: numPlayers }).map((_, i) => (
            <tr key={i}>
              <td>
                {i === 0 ? (
                  <input type="text" value={user.username} disabled />
                ) : (
                  <select
                    value={selectedUsers[i] || ""}
                    onChange={(e) => handleUserChange(i, e.target.value)}
                  >
                    <option value="">Select user</option>
                    {allUsers.map(u => (
                      <option key={u.id} value={u.id}>{u.username}</option>
                    ))}
                  </select>
                )}
              </td>
              <td>
                <input
                  type="number"
                  min="1"
                  max="10"
                  value={userPoints[i] || 0}
                  onChange={(e) => handlePointsChange(i, e.target.value)}
                />
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <button onClick={handleAddGame}>Add Game</button>
    </div>
  );
}

export default NewGamePage;
