import React, { useEffect, useState } from "react";
import axios from "axios";
import "../App.css";

function OrganizationPage({ user }) {
  const [organization, setOrganization] = useState(null);
  const [members, setMembers] = useState([]);
  const [availableUsers, setAvailableUsers] = useState([]);

  // Add user dropdown
  const [selectedAddUser, setSelectedAddUser] = useState("");

  // Remove user dropdown
  const [selectedRemoveUser, setSelectedRemoveUser] = useState("");

  // Tournament form state
  const [tournamentName, setTournamentName] = useState("");
  const [tournamentDesc, setTournamentDesc] = useState("");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [maxPlayers, setMaxPlayers] = useState("");

  const isAdmin = user?.role === "admin";

  useEffect(() => {
    const loadData = async () => {
      if (!user?.organization_id) return;

      try {
        // Fetch organization info
        const orgRes = await axios.get(
          `http://localhost:5000/api/organizations/${user.organization_id}`
        );
        setOrganization(orgRes.data);

        // Fetch organization members
        const membersRes = await axios.get(
          `http://localhost:5000/api/organizations/${user.organization_id}/users`
        );
        setMembers(membersRes.data);

        // Fetch all users NOT in an organization
        const usersRes = await axios.get("http://localhost:5000/api/users");
        const freeUsers = usersRes.data.filter(
          (u) => u.organization_id === null
        );
        setAvailableUsers(freeUsers);
      } catch (err) {
        console.error(err);
      }
    };

    loadData();
  }, [user]);

  // Add user to organization
  const handleAddUser = async () => {
    if (!selectedAddUser) return;

    try {
      await axios.put("http://localhost:5000/api/organizations/addUser", {
        userId: selectedAddUser,
        organizationId: user.organization_id,
      });

      const added = availableUsers.find((u) => u.id === parseInt(selectedAddUser));
      setMembers([...members, added]);
      setAvailableUsers(
        availableUsers.filter((u) => u.id !== parseInt(selectedAddUser))
      );

      setSelectedAddUser("");
    } catch (err) {
      console.error(err);
      alert("Failed to add user");
    }
  };

  // Remove user from organization
  const handleRemoveUser = async () => {
    if (!selectedRemoveUser) return;

    try {
      await axios.put("http://localhost:5000/api/organizations/removeUser", {
        userId: selectedRemoveUser
      });

      const removed = members.find((u) => u.id === parseInt(selectedRemoveUser));

      // Remove from members and add back to available users
      setMembers(members.filter((u) => u.id !== removed.id));
      setAvailableUsers([...availableUsers, removed]);

      setSelectedRemoveUser("");
    } catch (err) {
      console.error(err);
      alert("Failed to remove user");
    }
  };

  // Create a tournament
  const handleCreateTournament = async (e) => {
    e.preventDefault();

    try {
      await axios.post("http://localhost:5000/api/tournaments", {
        name: tournamentName,
        description: tournamentDesc,
        startDate,
        endDate,
        maxPlayers,
        organizationId: user.organization_id
      });

      alert("Tournament created!");
      
      setTournamentName("");
      setTournamentDesc("");
      setStartDate("");
      setEndDate("");
      setMaxPlayers("");
    } catch (err) {
      console.error(err);
      alert("Failed to create tournament");
    }
  };

  if (!organization) {
    return <h2>Loading...</h2>;
  }

  return (
    <div className="organization-container">
      <h1>{organization.name}</h1>

      <h2>Members</h2>
      <ul>
        {members.map((m) => (
          <li key={m.id}>
            {m.username} {m.role ? `(${m.role})` : ""}
          </li>
        ))}
      </ul>

      {/* Admin-only user management */}
      {isAdmin && (
        <>
          <div className="org-section">
            <h3>Add User to Organization</h3>
            <select
              value={selectedAddUser}
              onChange={(e) => setSelectedAddUser(e.target.value)}
            >
              <option value="">Select user</option>
              {availableUsers.map((u) => (
                <option key={u.id} value={u.id}>
                  {u.username}
                </option>
              ))}
            </select>
            <button onClick={handleAddUser}>Add</button>
          </div>

          <div className="org-section">
            <h3>Remove User from Organization</h3>
            <select
              value={selectedRemoveUser}
              onChange={(e) => setSelectedRemoveUser(e.target.value)}
            >
              <option value="">Select member</option>
              {members.map((m) => (
                <option key={m.id} value={m.id}>
                  {m.username}
                </option>
              ))}
            </select>
            <button onClick={handleRemoveUser}>Remove</button>
          </div>

          {/* Create Tournament (admin only) */}
          <div className="org-section">
            <h2>Create Tournament</h2>
            <form onSubmit={handleCreateTournament}>
              <input
                type="text"
                placeholder="Tournament Name"
                value={tournamentName}
                onChange={(e) => setTournamentName(e.target.value)}
                required
              />

              <textarea
                placeholder="Description"
                value={tournamentDesc}
                onChange={(e) => setTournamentDesc(e.target.value)}
                required
              />

              <label>Start Date</label>
              <input
                type="date"
                value={startDate}
                onChange={(e) => setStartDate(e.target.value)}
                required
              />

              <label>End Date</label>
              <input
                type="date"
                value={endDate}
                onChange={(e) => setEndDate(e.target.value)}
                required
              />

              <input
                type="number"
                placeholder="Max Players"
                value={maxPlayers}
                onChange={(e) => setMaxPlayers(e.target.value)}
                required
              />

              <button type="submit">Create Tournament</button>
            </form>
          </div>
        </>
      )}
    </div>
  );
}

export default OrganizationPage;
